import React from "react";
import { faTimesCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function Modal({ children, handleClose }) {
  return (
    <div className="absolute w-full bg-white/90 min-h-screen z-50">
      <div className="relative border shadow-2xl rounded-xl bg-white">
        <FontAwesomeIcon
          icon={faTimesCircle}
          size="lg"
          className="absolute text-black cursor-pointer top-1 right-1 hover:opacity-70 md:top-5 md:right-5"
          role="button"
          onClick={() => {
            handleClose();
          }}
        />
        <div className="py-4">{children}</div>
      </div>
    </div>
  );
}
