import axios from "axios";
import { API_URL } from "../utils/helpers";
import { useState } from "react";
import toast from "react-hot-toast";
import Modal from "./Modal";
import { useNavigate } from "react-router-dom";

export default function Todo({ todo, updateTodos }) {
  const navigate = useNavigate();
  const [isOpenDeleting, setIsOpenDeleting] = useState(false);

  const handleDelete = async () => {
    try {
      await axios.delete(`${API_URL}/${todo.id}`);
      updateTodos((prev) => prev.filter((t) => t.id !== todo.id));
    } catch (err) {
      if (err.response.status === 404) {
        toast.error("To-Do not found");
      } else {
        toast.error(
          err.response.data.detail ||
            "An error occurred. Please check your internet connection"
        );
      }
    }
  };

  return (
    <>
      {isOpenDeleting && (
        <Modal handleClose={() => setIsOpenDeleting(false)}>
          <div className="w-full text-center md:flex md:flex-col md:items-center md:justify-center px-8 py-12">
            <h2 className="text-2xl font-medium">
              Are you sure you want to log out?
            </h2>
            <br />
            <div className="flex flex-col items-center w-full space-y-6 ">
              <button
                className="w-full px-4 py-2 text-white bg-red-500 rounded-lg hover:bg-red-600 hover:-translate-y-1 duration-200 xl:w-3/4"
                onClick={() => {
                  navigate("/");
                  setIsOpenDeleting(false);
                }}>
                Yes
              </button>
              <button
                className="w-full px-4 py-2 text-white bg-gray-500 rounded-lg hover:bg-gray-600 hover:-translate-y-1 duration-200 xl:w-3/4"
                onClick={() => setIsOpenDeleting(false)}>
                No
              </button>
            </div>
          </div>
        </Modal>
      )}
      <li className="flex flex-col items-center space-y-4">
        <h2 className="font-bold text-xl">{todo.title}</h2>
        <p>{todo.completed ? "Completed" : "Not Completed"}</p>
        <button
          onClick={() => setIsOpenDeleting(true)}
          className="bg-red-500 text-white p-2 rounded-xl hover:bg-red-600 duration-200 w-full">
          Delete
        </button>
      </li>
    </>
  );
}
