import { useEffect, useState } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import NotLoggedIn from "./routes/NotLoggedIn";
import LoggedIn from "./routes/LoggedIn";
import { Toaster } from "react-hot-toast";
import toast from "react-hot-toast";
import {
  initAxiosInterceptors,
  getUser,
  deleteUser,
  API_URL,
  setUser,
} from "./utils/helpers";
import axios from "axios";

initAxiosInterceptors();

export default function App() {
  const [userInformation, setUserInformation] = useState(null);

  const handleLogout = (message = "You have been logged out") => {
    setUserInformation();
    deleteUser();
    toast(message, {
      icon: "ℹ️",
    });
  };

  useEffect(() => {
    const user = getUser();
    if (user && user.access_token && user.user) {
      setUserInformation(user);
    } else {
      setUserInformation(null);
      deleteUser();
      return;
    }
    const loadUser = async () => {
      setUserInformation(user);
      try {
        const response = await axios.get(`${API_URL}/auth/whoami`);
        console.log(response);
        // setUserInformation(data);
        // setUser(data);
      } catch (err) {
        if (err.response && err.response.status === 401) {
          handleLogout("Your session has expired, please login again");
        }
        toast.error("An unexpected error has occurred, please try again");
      }
    };
    loadUser();
  }, []);

  return (
    <>
      <div>
        <Toaster />
      </div>
      <Router>
        {userInformation ? (
          <LoggedIn
            userInformation={userInformation}
            setUserInformation={setUserInformation}
          />
        ) : (
          <NotLoggedIn setUserInformation={setUserInformation} />
        )}
      </Router>
    </>
  );
}
