import axios from "axios";
import { API_URL } from "../utils/helpers";
import { useState } from "react";
import toast from "react-hot-toast";

export default function Todo({ todo, updateTodos }) {
  const [isOpenDeleting, setIsOpenDeleting] = useState(false);

  const handleDelete = async () => {
    try {
      await axios.delete(`${API_URL}/${todo.id}`);
      updateTodos((prev) => prev.filter((t) => t.id !== todo.id));
    } catch (err) {
      if (err.response.status === 404) {
        toast.error("To-Do not found");
      } else {
        toast.error(
          err.response.data.detail ||
            "An error occurred. Please check your internet connection"
        );
      }
    }
  };

  return (
    <li className="flex flex-col items-center space-y-4">
      <h2 className="font-bold text-xl">{todo.title}</h2>
      <p>{todo.completed ? "Completed" : "Not Completed"}</p>
      <button
        onClick={handleDelete}
        className="bg-red-500 text-white p-2 rounded-xl hover:bg-red-600 duration-200 w-full">
        Delete
      </button>
    </li>
  );
}
