import { useState, useEffect } from "react";
import axios from "axios";
import { API_URL } from "../utils/helpers";

export default function Main() {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    const loadTodos = async () => {
      try {
        const { data } = await axios.get(`${API_URL}/`);
        setTodos(data);
      } catch (error) {
        console.error(error);
      }
    };

    loadTodos();
  }, []);

  return (
    <main className="py-6 px-4 xl:px-16 flex flex-col items-center">
      <h1 className="font-bold text-4xl">To-Do List</h1>
      {todos.length === 0 && (
        <section className="bg-gray-100 my-12 px-8 py-6 rounded-lg shadow-2xl border-2 border-black flex flex-col items-center">
          <p className="py-4">You do not have any todos on your list.</p>
          <p className="py-4">Please add a new todo</p>
          <button className="bg-blue-500 text-white p-4 rounded-xl font-medium shadow-lg">
            Add new todo
          </button>
        </section>
      )}
      {todos.length > 0 && (
        <section className="bg-gray-100 my-12 px-8 py-6 rounded-lg shadow-2xl border-2 border-black">
          <ul className="space-y-4">
            {todos.map((todo) => (
              <li key={todo.id} className="flex justify-between">
                <p>{todo.title}</p>
                <p>{todo.completed ? "Completed" : "Not Completed"}</p>
              </li>
            ))}
          </ul>
        </section>
      )}
    </main>
  );
}
